// Elementos do DOM
const form = document.getElementById('astralForm');
const generateBtn = document.getElementById('generateBtn');
const btnText = document.getElementById('btnText');
const loading = document.getElementById('loading');
const results = document.getElementById('results');
const error = document.getElementById('error');
const errorMessage = document.getElementById('errorMessage');

// URL base da API
const API_BASE_URL = window.location.origin;

// Event listener para o formul√°rio
form.addEventListener('submit', async (e) => {
    e.preventDefault();
    await generateBirthChart();
});

// Fun√ß√£o principal para gerar o mapa astral
async function generateBirthChart() {
    try {
        showLoading(true);
        hideError();
        hideResults();

        const formData = getFormData();
        const birthChartData = await callBirthChartAPI(formData);

        displayResults(birthChartData, formData);
        showResults();

    } catch (err) {
        console.error('Erro ao gerar mapa astral:', err);
        showError(err.message || 'Erro interno. Tente novamente.');
    } finally {
        showLoading(false);
    }
}

// Capturar dados do formul√°rio
function getFormData() {
    const formData = new FormData(form);
    const date = new Date(formData.get('date') + 'T' + formData.get('time'));

    return {
        name: formData.get('name'),
        year: date.getFullYear(),
        month: date.getMonth() + 1,
        day: date.getDate(),
        hour: date.getHours(),
        minute: date.getMinutes(),
        city: formData.get('city'),
        nation: 'BR', // Pode ser ajustado baseado na cidade se necess√°rio
        theme: formData.get('theme') || 'light'
    };
}

// Chamar API de mapa astral
async function callBirthChartAPI(data) {
    const requestBody = {
        subject: {
            name: data.name,
            year: data.year,
            month: data.month,
            day: data.day,
            hour: data.hour,
            minute: data.minute,
            city: data.city,
            nation: data.nation,
            geonames_username: true, // Sinaliza para usar o username configurado no servidor
            zodiac_type: 'Tropic'
        },
        theme: data.theme,
        language: 'PT'
    };

    const response = await fetch(`${API_BASE_URL}/api/v4/birth-chart`, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify(requestBody)
    });

    if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || `Erro HTTP: ${response.status}`);
    }

    return await response.json();
}

// Exibir resultados
function displayResults(data, formData) {
    // Informa√ß√µes do usu√°rio
    document.getElementById('resultName').textContent = formData.name;
    document.getElementById('resultDate').textContent = formatDate(formData);

    // SVG do mapa astral
    document.getElementById('chartSvg').innerHTML = data.chart;

    // Dados astrol√≥gicos
    displayAstrologicalData(data.data);

    // Aspectos
    displayAspects(data.aspects);
}

// Formatear data para exibi√ß√£o
function formatDate(data) {
    return `${data.day.toString().padStart(2, '0')}/${data.month.toString().padStart(2, '0')}/${data.year} √†s ${data.hour.toString().padStart(2, '0')}:${data.minute.toString().padStart(2, '0')}`;
}

// Exibir dados astrol√≥gicos
function displayAstrologicalData(data) {
    const container = document.getElementById('astrologicalData');

    if (!data || !data.planets_degrees) {
        container.innerHTML = '<p>Dados astrol√≥gicos n√£o dispon√≠veis.</p>';
        return;
    }

    const planetNames = {
        'Sun': 'Sol',
        'Moon': 'Lua',
        'Mercury': 'Merc√∫rio',
        'Venus': 'V√™nus',
        'Mars': 'Marte',
        'Jupiter': 'J√∫piter',
        'Saturn': 'Saturno',
        'Uranus': 'Urano',
        'Neptune': 'Netuno',
        'Pluto': 'Plut√£o',
        'Mean_Node': 'Nodo Norte',
        'Chiron': 'Qu√≠ron',
        'Ascendant': 'Ascendente',
        'Medium_Coeli': 'Meio do C√©u'
    };

    const signNames = {
        'Ari': '√Åries',
        'Tau': 'Touro',
        'Gem': 'G√™meos',
        'Can': 'C√¢ncer',
        'Leo': 'Le√£o',
        'Vir': 'Virgem',
        'Lib': 'Libra',
        'Sco': 'Escorpi√£o',
        'Sag': 'Sagit√°rio',
        'Cap': 'Capric√≥rnio',
        'Aqu': 'Aqu√°rio',
        'Pis': 'Peixes'
    };

    let html = '<div class="planet-info">';

    Object.entries(data.planets_degrees).forEach(([planet, info]) => {
        const planetName = planetNames[planet] || planet;
        const signName = signNames[info.sign] || info.sign || 'N/A';

        html += `
            <div class="planet-card">
                <div class="planet-name">${planetName}</div>
                <div class="planet-details">
                    Signo: ${signName}<br>
                    Grau: ${(info.abs_pos || 0).toFixed(2)}¬∞<br>
                    Casa: ${info.house || 'N/A'}
                </div>
            </div>
        `;
    });

    html += '</div>';
    container.innerHTML = html;
}

// Exibir aspectos
function displayAspects(aspects) {
    const container = document.getElementById('aspectsData');

    if (!aspects || aspects.length === 0) {
        container.innerHTML = '<p>Nenhum aspecto encontrado.</p>';
        return;
    }

    const aspectNames = {
        'conjunction': 'Conjun√ß√£o',
        'opposition': 'Oposi√ß√£o',
        'trine': 'Tr√≠gono',
        'square': 'Quadratura',
        'sextile': 'Sextil',
        'quintile': 'Quintil',
        'semi_sextile': 'Semi-sextil',
        'quincunx': 'Quincunx'
    };

    const planetNames = {
        'Sun': 'Sol',
        'Moon': 'Lua',
        'Mercury': 'Merc√∫rio',
        'Venus': 'V√™nus',
        'Mars': 'Marte',
        'Jupiter': 'J√∫piter',
        'Saturn': 'Saturno',
        'Uranus': 'Urano',
        'Neptune': 'Netuno',
        'Pluto': 'Plut√£o',
        'Mean_Node': 'Nodo Norte',
        'Chiron': 'Qu√≠ron',
        'Ascendant': 'Ascendente',
        'Medium_Coeli': 'Meio do C√©u'
    };

    let html = '<div class="aspects-grid">';

    aspects.forEach(aspect => {
        const aspectName = aspectNames[aspect.aspect] || aspect.aspect;
        const planet1 = planetNames[aspect.p1_name] || aspect.p1_name;
        const planet2 = planetNames[aspect.p2_name] || aspect.p2_name;

        html += `
            <div class="aspect-card">
                <div class="aspect-type">${aspectName}</div>
                <div class="aspect-details">
                    ${planet1} ‚Üî ${planet2}<br>
                    Orbe: ${(aspect.orbit || aspect.orb || 0).toFixed(2)}¬∞
                </div>
            </div>
        `;
    });

    html += '</div>';
    container.innerHTML = html;
}

// Fun√ß√£o para mostrar/esconder loading
function showLoading(show) {
    if (show) {
        btnText.classList.add('hidden');
        loading.classList.remove('hidden');
        generateBtn.disabled = true;
    } else {
        btnText.classList.remove('hidden');
        loading.classList.add('hidden');
        generateBtn.disabled = false;
    }
}

// Fun√ß√£o para mostrar erro
function showError(message) {
    errorMessage.textContent = message;
    error.classList.remove('hidden');
}

// Fun√ß√£o para esconder erro
function hideError() {
    error.classList.add('hidden');
}

// Fun√ß√£o para mostrar resultados
function showResults() {
    results.classList.remove('hidden');
    results.scrollIntoView({ behavior: 'smooth' });
}

// Fun√ß√£o para esconder resultados
function hideResults() {
    results.classList.add('hidden');
}

// Configura√ß√µes iniciais
document.addEventListener('DOMContentLoaded', () => {
    // N√£o √© mais necess√°rio pr√©-preencher coordenadas
    // O Geonames far√° a geocodifica√ß√£o automaticamente
});

frontend/styles.css.content:
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    min-height: 100vh;
    color: #333;
}

.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 20px;
}

header {
    text-align: center;
    margin-bottom: 40px;
    color: white;
}

header h1 {
    font-size: 2.5rem;
    margin-bottom: 10px;
    text-shadow: 2px 2px 4px rgba(0,0,0,0.3);
}

header p {
    font-size: 1.1rem;
    opacity: 0.9;
}

.form-container {
    background: white;
    border-radius: 20px;
    padding: 40px;
    box-shadow: 0 20px 40px rgba(0,0,0,0.1);
    margin-bottom: 30px;
}

.form-group {
    margin-bottom: 25px;
}

.form-row {
    display: flex;
    gap: 20px;
}

.form-row .form-group {
    flex: 1;
}

label {
    display: block;
    margin-bottom: 8px;
    font-weight: 600;
    color: #555;
}

input, select {
    width: 100%;
    padding: 12px 16px;
    border: 2px solid #e1e5e9;
    border-radius: 10px;
    font-size: 16px;
    transition: all 0.3s ease;
    background: #f8f9fa;
}

input:focus, select:focus {
    outline: none;
    border-color: #667eea;
    background: white;
    box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
}

button {
    width: 100%;
    padding: 15px;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;
    border: none;
    border-radius: 10px;
    font-size: 18px;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
    position: relative;
    overflow: hidden;
}

button:hover {
    transform: translateY(-2px);
    box-shadow: 0 10px 25px rgba(102, 126, 234, 0.3);
}

button:disabled {
    opacity: 0.7;
    cursor: not-allowed;
    transform: none;
}

.loading {
    display: flex;
    align-items: center;
    justify-content: center;
}

.spinner {
    width: 20px;
    height: 20px;
    border: 2px solid rgba(255,255,255,0.3);
    border-top: 2px solid white;
    border-radius: 50%;
    animation: spin 1s linear infinite;
}

@keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
}

.hidden {
    display: none !important;
}

.results {
    background: white;
    border-radius: 20px;
    padding: 40px;
    box-shadow: 0 20px 40px rgba(0,0,0,0.1);
    margin-bottom: 30px;
}

.result-header {
    text-align: center;
    margin-bottom: 30px;
    padding-bottom: 20px;
    border-bottom: 2px solid #f1f3f4;
}

.result-header h2 {
    color: #667eea;
    font-size: 2rem;
    margin-bottom: 10px;
}

.result-info {
    display: flex;
    justify-content: center;
    gap: 20px;
    font-weight: 500;
    color: #666;
}

.chart-container {
    text-align: center;
    margin: 30px 0;
    padding: 20px;
    background: #f8f9fa;
    border-radius: 15px;
}

.chart-container svg {
    max-width: 100%;
    height: auto;
    border-radius: 10px;
}

.data-container, .aspects-container {
    margin-top: 30px;
}

.data-container h3, .aspects-container h3 {
    color: #667eea;
    margin-bottom: 20px;
    font-size: 1.3rem;
}

.planet-info {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 15px;
    margin-bottom: 20px;
}

.planet-card {
    background: #f8f9fa;
    padding: 15px;
    border-radius: 10px;
    border-left: 4px solid #667eea;
}

.planet-name {
    font-weight: 600;
    color: #333;
    margin-bottom: 5px;
}

.planet-details {
    color: #666;
    font-size: 0.9rem;
}

.aspects-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 15px;
}

.aspect-card {
    background: #f8f9fa;
    padding: 15px;
    border-radius: 10px;
    border-left: 4px solid #764ba2;
}

.aspect-type {
    font-weight: 600;
    color: #333;
    margin-bottom: 5px;
}

.aspect-details {
    color: #666;
    font-size: 0.9rem;
}

.error {
    background: #ffe6e6;
    color: #d63031;
    padding: 20px;
    border-radius: 10px;
    border: 1px solid #fab1a0;
    text-align: center;
    margin-bottom: 30px;
}

footer {
    text-align: center;
    color: white;
    opacity: 0.8;
    margin-top: 40px;
}

@media (max-width: 768px) {
    .container {
        padding: 15px;
    }

    header h1 {
        font-size: 2rem;
    }

    .form-container {
        padding: 30px 20px;
    }

    .form-row {
        flex-direction: column;
        gap: 0;
    }

    .result-info {
        flex-direction: column;
        gap: 10px;
    }

    .planet-info {
        grid-template-columns: 1fr;
    }

    .aspects-grid {
        grid-template-columns: 1fr;
    }
}

